const getSchemas = Schema => {
    const BranchSchema = new Schema({
      name: String,
      storeCode: String,
      magentoCode: String,
      description: String,
      address1: String,
      address2: String,
      location: {
        type: { type: String },
        coordinates: []
      },
      lng: String,
      lat: String,
      phone: String,
      email: String,
      status: { type: String, default: "ACTIVE" },
    })
  
  
    return {
      BranchSchema,
    }
  }
  
  module.exports = getSchemas
  