const { getSchemas } = require("../index")
const { getMdlFromSchemas } = require("../../utils")

const getModel = ({ dbConn, Schema }) => {
  const targetSchemas = ["GiftBasketSchema", "GiftBasketItemSchema", "GiftBasketGifteeSchema"]

  return {
    ...getMdlFromSchemas({ dbConn, Schema, getSchemas })(targetSchemas),
  }
}

module.exports = getModel