const getSchemas = (Schema) => {
    //Prestige, SMAC or ACE Rewards
    const LoyaltySchema = new Schema({
      userId: { type: String }, // { type: String, index: true }, //userId: String, from estore-graph and sm-store-job Extracted for external index
      name: String,
      type: String,
      cardNumber: String,
      expiry: Date,
      dateAdded: Date,
      transactionCode: String,
      transactionDate: Date,
      pickupBranch: String,
      seqNo: String,
      updated: String,
      point: Number,
      isExpired: { type: Boolean, default: false },
    })
  
    return {
      LoyaltySchema,
    }
  }
  
  module.exports = getSchemas
  