const getSchemas = (Schema) => {

    const ReportSchema = new Schema({
        createdDate: Date,
        isDone: { type: Boolean, default: false },
        userId: { type: String, required: true },
        report: { type: String },
        parameters: { type: String },
        downloadLink: { type: String }
    })

    return {
        ReportSchema
    }
}

module.exports = getSchemas
