const getSchemas = (Schema) => {

    const UserSchema = new Schema({
        username: { type: String }, // { type: String, unique: true }, Extracted for external index
        password: String,
        firstName: String,
        lastName: String,
        email: { type: String }, // { type: String, unique: true }, Extracted for external index
        branch: String,
        lastPasswordUpdate: Date,
        usedPasswords: [String],
        loginAttemptsCount: Number,
        lastLoginAttempt: Date,
        status: { type: String, enum: ["Active", "Disabled"], default: "Active" },
        count: { type: Number, default: 0 }, // used by jwt refresh token
        disabledDate: Date,
        role: { type: String, enum: ["Admin", "User", "Accounting"], default: "User" },
    })


    return {
        UserSchema
    }
}

module.exports = getSchemas
